    public boolean createTable() {
        boolean result = false;
        
        boolean step_a = sqlExecutor(config.getSQLTmplTableDrop(this.tableName));
        boolean step_b = sqlExecutor(config.getSQLTmplTableCreate(this.tableName, __COLUM_NAMES));
        if (step_a && step_b) {
        	result = true;
        }else {
        	logger.error("Create Table Error.");
        }
        
        return result;
    }
    
    public boolean createTable(String tableName) {
    	boolean result = false;
    	if (!Strings.isNullOrEmpty(tableName)) {
    		String str_a = String.format(sql_template_table_drop, tableName);
    		String str_b = String.format(sql_template_table_create, tableName, __COLUM_NAMES);
    		boolean step_a = sqlExecutor(str_a);
    		boolean step_b = sqlExecutor(str_b);
    		if (step_a && step_b) {
    			result = true;
    		}else {
    			logger.error("Create Table Error.");
    		}
    	}
    	return result;
    }
    
    private boolean sqlExecutor(String sql) {
        boolean result = false;
        if (null != this.stmt && !Strings.isNullOrEmpty(sql)) {
            try {
                stmt.execute(sql);
                result = true;
            } catch (SQLException e) {
                logger.error("Execute SQL Error", e);
            }
        }
        return result;
    }
    
    private ResultSet queryExecutor(String sql) {
        if (null != this.conn && !Strings.isNullOrEmpty(sql)) {
            PreparedStatement psmt = null;
            try {
                psmt = this.conn.prepareStatement(sql);
                rst = psmt.executeQuery();
            } catch (SQLException e) {
                logger.error("SQL Query Error", e);
            }
        }
        
        return rst;
    }
    private void closeConn(Connection conn, Statement stmt, ResultSet rst) {
        if (rst != null) {
            try {
                rst.close();
                logger.info("SQL ResultSet Closed.");
            } catch (SQLException e) {
                logger.error("", e);
            }
        }

        if (stmt != null) {
            try {
                stmt.close();
                logger.info("SQL Statement Closed.");
            } catch (SQLException e) {
                logger.error("", e);
            }
        }

        if (conn != null) {
            try {
                conn.close();
                logger.info("H2DB-Conn Closed.");
            } catch (SQLException e) {
                logger.error("", e);
            }
        }
    }
    
    private String getSQLValues() {
        StringBuilder sql_kit_values = new StringBuilder();
        if (null != this.value_map && this.value_map.size() > 0) {
            for (Entry<String, Double> entry : this.value_map.entrySet()) {
                String key = entry.getKey();
                String currency_from = key.substring(0, 3);
                String currency_to = key.substring(3, key.length());
                
                // Double rrate = entry.getValue();
                Double rate_from_to = entry.getValue();
                Double rate_usd_to = this.value_map.get(__CURRENCY_USD + currency_to);
                if (null == rate_usd_to) {
                    rate_usd_to = 1.0;
                }
                /*
                 * (CURRENCY_FROM, CURRENCY_TO, RATE_FROM_TO, RATE_USD_TO)
                 * "('DDY','DIY','0.3399','1.12389'),"
                 */
                String values_meta = "('" + currency_from + "','" + currency_to + "','" + rate_from_to + "','" + rate_usd_to + "'),";
                sql_kit_values.append(values_meta);
            }
        }
        return sql_kit_values.toString();
    }
    
    private String buildInsertSQLHeader() {
        StringBuilder sql_builder = new StringBuilder();
        sql_builder.append(String.format("INSERT INTO %s", this.tableName));
        sql_builder.append("(CURRENCY_FROM, CURRENCY_TO, RATE_FROM_TO, RATE_USD_TO) ");
        sql_builder.append("VALUES");
        return sql_builder.toString();
    }
    
